                              @input="$v.profile.city_id.$touch()"
                              @blur="$v.profile.city_id.$touch()"
                              
                              class="form-control"></b-form-select>
                              <span v-if="!$v.profile.city_id.required && $v.profile.city_id.$error" class="error-msg">City is required</span>
                              <!-- <span class="custommsg" ><p v-html="cityMsg"></p></span> -->

