  </template>
  <script>
  var base_url = 'http://qapin_mvp5.manageprojects.in/';
  
  import { validationMixin } from 'vuelidate'
  import { required, email, alpha,minLength,maxLength } from 'vuelidate/lib/validators'
  import axios from 'axios' 
  import { mapActions } from 'vuex'
  // import { EyeIcon, EyeOffIcon } from '@vue-hero-icons/outline'

  export default {
    title: 'Create an Account | Qapin',

    mixins: [validationMixin],

    validations: {
      useremail:{email: { required, email}},
      user:{
        first_name: { required, alpha,
                   maxLength: maxLength(20) 
               },
        last_name: { required, alpha,
                  maxLength: maxLength(20) 
               },
        email: { required, email},
        password: { required,
                  minLength: minLength(6),
                  maxLength: maxLength(16) 
              },
        term_condition: { required },
      },
      profile:{
        quality_professional_flag:{ required },
        dob:{ required },
        country_id:{ required },
        state_id:{ required },
        city_id:{ required },
        gender:{ required },
      }
    },
    // components: {
    //   EyeIcon,
    //   EyeOffIcon,
    // },

    data() {
      return { 
         
       max: new Date(new Date().getFullYear()-18, 1, 31),
        useremail:{
          email:''
        },
        user:{
          first_name: '',
          last_name: '',
          email: '',
          password: '',
          term_condition: '',
        },
        profile:{
          nationality:'',
          city_id: '',
          country_id: '',
          dob: '',
          gender: '',
          qp_role: '',
          quality_professional_flag: '',
          state_id: '',
        },
        message:false,
        radioerror:'',
        firstnameMsg:'',
        lastnameMsg:'',
        passwordMsg:'',
        termsconditionMsg:'',
        welcomeMsg:'',
        dobMsg:'',
        genderMsg:'',
        countryMsg:'',
        stateMsg:'',
        cityMsg:'',
        isHidden: true,
        nextValue:1,
        roleStatus :false,
        showPassword:false,
        step:1,
        selected: 'A',
        image: this.$store.state.baseUrlImg+"upload/freelancer/register1-bg.png",
        genders: [
          { value: null, text: 'Please select an option' },
          { value: 'M', text: 'Male' },
          { value: 'F', text: 'Female' },
        ],
        country: [],
        state: [],
        city: [],
        role: []
      }
    },
    watch : {
      
    },     
    computed: {
      emailErrors() {
        const errors = []
        if (!this.$v.useremail.email.$dirty) return errors
        !this.$v.useremail.email.email && errors.push('Must be valid e-mail')
        !this.$v.useremail.email.required && errors.push('E-mail is required')
        return errors
      },
      firstNameErrors() {
        const errors = []
        if (!this.$v.user.first_name.$dirty) return errors
        !this.$v.user.first_name.alpha && errors.push('Sorry! No special characters or numbers');
        !this.$v.user.first_name.required && errors.push('First Name is required');
        !this.$v.user.first_name.maxLength && errors.push("Too long. Use at least 20 characters or less");
        return errors
      },
      lastNameErrors() {
        const errors = []
        if (!this.$v.user.last_name.$dirty) return errors
        !this.$v.user.last_name.alpha && errors.push('Sorry! No special characters or numbers')
        !this.$v.user.last_name.required && errors.push('Last Name is required')
        !this.$v.user.last_name.maxLength && errors.push("Too long. Use at least 20 characters or less");
        return errors
      },
      passwordErrors() {
        const errors = []
        if (!this.$v.user.password.$dirty) return errors
        !this.$v.user.password.required && errors.push('Password is required');
        !this.$v.user.password.minLength && errors.push("Password must be minimum 6 characters"); 
        !this.$v.user.password.maxLength && errors.push("Password must be maximum 16 characters");
        return errors
      },
      termsconditionErrors(){
        const errors = []
        if (!this.$v.user.term_condition.$dirty) return errors
        !this.$v.user.term_condition.required && errors.push('Check Term & condition is required')
        return errors
      },
      doberror(){
        const errors = []
        if (!this.$v.profile.dob.$dirty) return errors
        !this.$v.profile.dob.required && errors.push('Date Of Birth is required')
        return errors
      },
      genderErrors(){
        const errors = []
        if (!this.$v.profile.gender.$dirty) return errors
        !this.$v.profile.gender.required && errors.push('Select Gender is required')
        return errors
      },
      countryErrors(){
        const errors = []
        if (!this.$v.profile.country_id.$dirty) return errors
        !this.$v.profile.country_id.required && errors.push('Select Country is required')
        return errors
      },
      stateErrors(){
        const errors = []
        if (!this.$v.profile.state_id.$dirty) return errors
        !this.$v.profile.state_id.required && errors.push('Select State is required')
        return errors
      },
      cityErrors(){
        const errors = []
        if (!this.$v.profile.city_id.$dirty) return errors
        !this.$v.profile.city_id.required && errors.push('Select City is required')
        return errors
      },
    },

    methods: {
      ...mapActions(["genericAPIPOSTRequest"]),
      gotoLogin(){
        this.$router.push('/login');
      },
      isUnique(){
        if (/^\w+([-]?\w+)*@\w+([-]?\w+)*(\.\w{2,3})+$/.test(this.useremail.email)) {
          axios.post(base_url+'api/user/checkMail', { email: this.useremail.email }).then((response) => {
            if('response',response.data.status == false){
              this.message=true;
              this.isHidden = false;
              this.nextValue=1;
            }else{
              this.message=false;
              this.isHidden = true;
              this.nextValue = 2; 
            }
          });
        }else{
          this.message=false;
          this.nextValue=1;
          this.isHidden = true;
        }
      },
      //this function is for step form next page
      async next(step){
        if(step==1){
          this.$v.useremail.$touch()
          this.image = this.$store.state.baseUrlImg+"upload/freelancer/register1-bg.png";
          this.step = step;
        }
        if(step==2){
          if(this.isHidden != true){
            return false;            
          }
          this.image = this.$store.state.baseUrlImg+"upload/freelancer/register1-bg.png";
          this.step = step;
        }
        if(step==3){
          if (this.profile.quality_professional_flag !="") {
            this.radioerror = "";
            if (this.profile.quality_professional_flag == 'Y') {
              var res = await this.genericAPIPOSTRequest({
                requestUrl: "common/getQpRoleMasterList",
                params: {"user_id":this.user_id},
              })
              if (res.length > 0)  {
                  this.role  =  res;
                  this.roleStatus = true;
                }else{
                  this.roleStatus = false;
                }
            }else{
              this.roleStatus = false;
            }
            this.step = step;
            this.image = this.$store.state.baseUrlImg+"upload/freelancer/register2-bg.png";
          }else{
            this.radioerror = "Checkbox is Required";
            return false;
          }
        }
        if(step==4){
          this.user.email = this.useremail.email;
          this.$v.user.$touch();
          //this code is for checking form value
          if (!this.$v.user.$invalid) {
              this.welcomeMsg=this.user.first_name;
              this.image = this.$store.state.baseUrlImg+"upload/freelancer/register2-bg.png";
              //this is for implement country api list
              var country = await this.genericAPIPOSTRequest({
                requestUrl: "common/getCountryList",
                params: {},
              })
              if (country.length > 0 ) {
                  this.country = country;
              }
              this.step = step;
            }
        }
      },
      //this function is for step form previous page
      pre(step){
        this.image = "https://picsum.photos/200/300";
        this.step = step;
      },
      // THIS FUNCTIONIS FOR GET STATE DATA
      async changecountry(event){
        var res = await this.genericAPIPOSTRequest({
          requestUrl:'common/getStateList',
          params:{ country_id: event }
        })
        if(res.length>0) {
          this.state = res;
        }
      },
      // THIS FUNCTIONIS FOR GET CITY DATA
      async changestate(event){
        var res = await this.genericAPIPOSTRequest({
          requestUrl:'common/getCityList',
          params:{ state_id: event }
        })
        if(res.length>0) {
          this.city = res;
        }
      },
      // THIS FUNCTIONIS FOR SUMIT FORM
      async submitLoginData(){ 
        this.$v.profile.$touch(); 
         if (this.$v.profile.$invalid) {
           this.$toastr.e('Please select all');
         }
        if (!this.$v.profile.$invalid) { 
          var res = await this.genericAPIPOSTRequest({
            requestUrl:'user/addUser',
            params:this.user
          })
          if (res) {
            this.profile.nationality = this.profile.country_id;
            var varToken = res.token;  
            axios.post(base_url+'api/user/updateProfile',this.profile,{
              headers: {
                Authorization: 'Bearer ' + varToken
              }
            }).then((res)=>{
              if (res.data.status === true) {
                this.$toastr.s('Registration successfull');
                this.$router.push({path: '/login'});
              }else{
                this.$toastr.e('Registration Unsuccessfull');
              }
            })
            
          }else{
            this.$toastr.e('Registration Unsuccessfull');
          }
        }
      }
    }
  }
  
  </script>
